constructor.parameter_list:
( const char * _version,
	const char * _encoding,
	const char * _standalone )

function.block:
{
	fprintf (cfile, "<?xml ");

	if ( !version.empty() )
		fprintf (cfile, "version=\"%s\" ", version.c_str ());
	if ( !encoding.empty() )
		fprintf (cfile, "encoding=\"%s\" ", encoding.c_str ());
	if ( !standalone.empty() )
		fprintf (cfile, "standalone=\"%s\" ", standalone.c_str ());
	fprintf (cfile, "?>");
}

{
	(*stream) << "<?xml ";

	if ( !version.empty() )
	{
		(*stream) << "version=\"";
		PutString( version, stream );
		(*stream) << "\" ";
	}
	if ( !encoding.empty() )
	{
		(*stream) << "encoding=\"";
		PutString( encoding, stream );
		(*stream ) << "\" ";
	}
	if ( !standalone.empty() )
	{
		(*stream) << "standalone=\"";
		PutString( standalone, stream );
		(*stream) << "\" ";
	}
	(*stream) << "?>";
}

{	
	TiXmlDeclarationA* clone = new TiXmlDeclarationA();

	if ( !clone )
		return 0;

	CopyToClone( clone );
	clone->version = version;
	clone->encoding = encoding;
	clone->standalone = standalone;
	return clone;
}

{
	for ( int i=0; i<depth; i++ )
		fprintf( cfile, "    " );
	fprintf( cfile, "%s", value.c_str() );
}

{
	(*stream) << "<" << value << ">";		// Don't use entities hear! It is unknown.
}

{
	TiXmlUnknownA* clone = new TiXmlUnknownA();

	if ( !clone )
		return 0;

	CopyToClone( clone );
	return clone;
}

{
	assert( !Find( addMe->Name() ) );	// Shouldn't be multiply adding to the set.

	addMe->next = &sentinel;
	addMe->prev = sentinel.prev;

	sentinel.prev->next = addMe;
	sentinel.prev      = addMe;
}

{
	TiXmlAttributeA* node;

	for( node = sentinel.next; node != &sentinel; node = node->next )
	{
		if ( node == removeMe )
		{
			node->prev->next = node->next;
			node->next->prev = node->prev;
			node->next = 0;
			node->prev = 0;
			return;
		}
	}
	assert( 0 );		// we tried to remove a non-linked attribute.
}

{
	TiXmlAttributeA* node;

	for( node = sentinel.next; node != &sentinel; node = node->next )
	{
		if ( node->name == name )
			return node;
	}
	return 0;
}

{
	TIXMLA_STRING tag;
	tag.reserve( 8 * 1000 );
	base.StreamIn( &in, &tag );

	base.Parse( tag.c_str(), 0 );
	return in;
}

{
	base.StreamOut (& out);
	return out;
}

{
   std::ostringstream os_stream( std::ostringstream::out );
   base.StreamOut( &os_stream );

   out.append( os_stream.str() );
   return out;
}

{
	if ( node )
	{
		TiXmlNodeA* child = node->FirstChild();
		if ( child )
			return TiXmlHandleA( child );
	}
	return TiXmlHandleA( 0 );
}

